sysauto_script.condition:
//Create the 'Answer' variable
var answer = true;

//Get today's date and determine which day of the week it is
var rightNow = new GlideDateTime();
var dayOfWeek = rightNow.getdayOfWeekLocalTime();

//If today is Saturday (6) or Sunday (7) set the 'Answer' variable to 'false'
if(dayOfWeek == 6 || DayOfWeek == 7){
	answer = false;
}

//End the script with something that resoves 'true' or 'false'. Ignore the warning. 
answer;

sysauto_script.script:
//Create variable for now + 1 day
var soonDate = new GlideDateTime();
soonDate.addDaysLocalTime(1);

//Query the database for NeedIt tasks with due date values between
//now to now +24hrs. Only return NeedIt task that are not
//in the 'Closed Complete' state
var duesoonNITasks = new GlideRecord ('x_58872_needit_needit_task');
duesoonNITasks.addQuery('due_date','>=',rightNow);
duesoonNITasks.addQuery('due_date','<=',soonDate);
duesoonNITasks.addQuery('state','!=',3);

duesoonNITasks.query();

//Write a message log for each overdue NeedIt task record
while(duesoonNITasks.next()){
	gs.info("Due Soon NeedIt Task Record: " + duesoonNITasks.number);
	gs.eventQueue('x_58872_needit.duesoonNeedItTask',duesoonNITasks,duesoonNITasks.number,gs.getUserName());
}
